import React, { useState } from 'react';

const SpanishPracticeChat = () => {
  const [messages, setMessages] = useState([
    {
      id: 1,
      text: "¡Hola! Soy tu compañero de práctica español. ¿Cómo estás hoy?",
      isAi: true
    }
  ]);
  const [inputText, setInputText] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const sendMessage = async (userMessage) => {
    try {
      setIsLoading(true);
      const response = await fetch('http://localhost:3001/api/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          message: userMessage,
          userId: 'user123' // In production, use actual user ID
        }),
      });

      const data = await response.json();
      return data.response;
    } catch (error) {
      console.error('Error:', error);
      return "Lo siento, hubo un error. Por favor, intenta de nuevo.";
    } finally {
      setIsLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!inputText.trim() || isLoading) return;

    const userMessage = {
      id: messages.length + 1,
      text: inputText,
      isAi: false
    };

    setMessages(prev => [...prev, userMessage]);
    setInputText('');

    const aiResponse = await sendMessage(inputText);

    const aiMessage = {
      id: messages.length + 2,
      text: aiResponse,
      isAi: true
    };

    setMessages(prev => [...prev, aiMessage]);
  };

  // ... rest of your styling code remains the same ...

  return (
    <div style={containerStyle}>
      <div style={chatContainerStyle}>
        <div style={messagesContainerStyle}>
          {messages.map((message) => (
            <div key={message.id} style={messageStyle(message.isAi)}>
              <div style={messageBubbleStyle(message.isAi)}>
                {message.text}
              </div>
            </div>
          ))}
        </div>

        <form onSubmit={handleSubmit} style={formStyle}>
          <input
            type="text"
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
            style={inputStyle}
            placeholder="Escribe tu mensaje aquí..."
            disabled={isLoading}
          />
          <button
            type="submit"
            style={{
              ...buttonStyle,
              opacity: isLoading ? 0.7 : 1,
              cursor: isLoading ? 'not-allowed' : 'pointer'
            }}
            disabled={isLoading}
          >
            {isLoading ? 'Enviando...' : 'Enviar'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default SpanishPracticeChat;
