import React, { useState } from 'react';

const SpanishPracticeChat = () => {
  const [messages, setMessages] = useState([
    {
      id: 1,
      text: "¡Hola! Soy tu compañero de práctica español. ¿Cómo estás hoy?",
      isAi: true
    }
  ]);
  const [inputText, setInputText] = useState('');

  // Simulate AI response - in a real app, this would call your backend
  const generateResponse = (userMessage) => {
    // Example responses - would be replaced with actual AI integration
    const responses = [
      "¡Qué interesante! Cuéntame más sobre eso.",
      "¿Podrías elaborar un poco más?",
      "Entiendo. ¿Y qué piensas sobre eso?",
      "¡Muy bien! Estás mejorando tu español.",
    ];
    return responses[Math.floor(Math.random() * responses.length)];
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!inputText.trim()) return;

    // Add user message
    const userMessage = {
      id: messages.length + 1,
      text: inputText,
      isAi: false
    };

    // Generate AI response
    const aiResponse = {
      id: messages.length + 2,
      text: generateResponse(inputText),
      isAi: true
    };

    setMessages([...messages, userMessage, aiResponse]);
    setInputText('');
  };

  return (
    <div className="max-w-2xl mx-auto p-4 bg-white rounded-lg shadow">
      <h1 className="text-2xl font-bold mb-4">Amigo</h1>

      <div
        style={{
          height: '400px',
          overflowY: 'auto',
          marginBottom: '1rem',
          padding: '1rem',
          backgroundColor: '#f9fafb'
        }}
      >
        {messages.map((message) => (
          <div
            key={message.id}
            style={{
              marginBottom: '1rem',
              textAlign: message.isAi ? 'left' : 'right'
            }}
          >
            <div
              style={{
                display: 'inline-block',
                padding: '0.75rem',
                borderRadius: '0.5rem',
                maxWidth: '80%',
                backgroundColor: message.isAi ? '#dbeafe' : '#dcfce7',
                color: message.isAi ? '#1e40af' : '#166534'
              }}
            >
              {message.text}
            </div>
          </div>
        ))}
      </div>

      <form onSubmit={handleSubmit} style={{ display: 'flex', gap: '0.5rem' }}>
        <input
          type="text"
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          style={{
            flexGrow: 1,
            padding: '0.5rem',
            border: '1px solid #d1d5db',
            borderRadius: '0.375rem',
            outline: 'none'
          }}
          placeholder="Escribe tu mensaje aquí..."
        />
        <button
          type="submit"
          style={{
            padding: '0.5rem 1rem',
            backgroundColor: '#3b82f6',
            color: 'white',
            borderRadius: '0.375rem',
            border: 'none',
            cursor: 'pointer'
          }}
        >
          Send
        </button>
      </form>
    </div>
  );
};

export default SpanishPracticeChat;
