import React, { useState } from 'react';
import { Send } from 'lucide-react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';

const SpanishPracticeChat = () => {
  const [messages, setMessages] = useState([
    {
      id: 1,
      text: "¡Hola! Soy tu compañero de práctica español. ¿Cómo estás hoy?",
      isAi: true
    }
  ]);
  const [inputText, setInputText] = useState('');

  // Simulate AI response - in a real app, this would call your backend
  const generateResponse = (userMessage) => {
    // Example responses - would be replaced with actual AI integration
    const responses = [
      "¡Qué interesante! Cuéntame más sobre eso.",
      "¿Podrías elaborar un poco más?",
      "Entiendo. ¿Y qué piensas sobre eso?",
      "¡Muy bien! Estás mejorando tu español.",
    ];
    return responses[Math.floor(Math.random() * responses.length)];
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!inputText.trim()) return;

    // Add user message
    const userMessage = {
      id: messages.length + 1,
      text: inputText,
      isAi: false
    };

    // Generate AI response
    const aiResponse = {
      id: messages.length + 2,
      text: generateResponse(inputText),
      isAi: true
    };

    setMessages([...messages, userMessage, aiResponse]);
    setInputText('');
  };

  return (
    <Card className="w-full max-w-2xl mx-auto">
      <CardHeader>
        <CardTitle>Spanish Practice Chat</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="h-96 overflow-y-auto mb-4 p-4 bg-gray-50 rounded-lg">
          {messages.map((message) => (
            <div
              key={message.id}
              className={`mb-4 ${message.isAi ? 'text-left' : 'text-right'
                }`}
            >
              <div
                className={`inline-block p-3 rounded-lg max-w-[80%] ${message.isAi
                  ? 'bg-blue-100 text-blue-900'
                  : 'bg-green-100 text-green-900'
                  }`}
              >
                {message.text}
              </div>
            </div>
          ))}
        </div>

        <form onSubmit={handleSubmit} className="flex gap-2">
          <input
            type="text"
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
            className="flex-1 p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            placeholder="Escribe tu mensaje aquí..."
          />
          <button
            type="submit"
            className="p-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            <Send className="w-5 h-5" />
          </button>
        </form>
      </CardContent>
    </Card>
  );
};

export default SpanishPracticeChat;
